INSERT INTO core_stylesheet (id_stylesheet, description, file_name, source) VALUES (10,'Rubrique HTML','portlet_html.xsl','<?xml version=\"1.0\"?>\r\n<xsl:stylesheet version=\"1.0\" xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\">\r\n<xsl:output method=\"html\" indent=\"yes\"/>\r\n\r\n<xsl:template match=\"portlet\">\r\n\r\n<xsl:variable name=\"device_class\">\r\n<xsl:choose>\r\n	<xsl:when test=\"string(display-on-small-device)=\'0\'\">hidden-phone</xsl:when>\r\n	<xsl:otherwise></xsl:otherwise>\r\n</xsl:choose>\r\n</xsl:variable>\r\n\r\n<div class=\"portlet {$device_class}\">\r\n\r\n<xsl:choose>\r\n	<xsl:when test=\"not(string(display-portlet-title)=\'1\')\">\r\n	<h1 class=\"portlet-header -lutece-border-radius-top\">\r\n	<xsl:value-of disable-output-escaping=\"yes\" select=\"portlet-name\" />\r\n	</h1>\r\n	<div class=\"portlet-content -lutece-border-radius-bottom\">\r\n		<xsl:apply-templates select=\"html-portlet\" />\r\n	</div>\r\n	</xsl:when>\r\n	<xsl:otherwise>\r\n	<div class=\"portlet-content -lutece-border-radius\">\r\n	<xsl:apply-templates select=\"html-portlet\" />\r\n	</div>\r\n	</xsl:otherwise>\r\n</xsl:choose>\r\n\r\n\r\n</div>\r\n</xsl:template>\r\n	\r\n<xsl:template match=\"html-portlet\">\r\n	<xsl:apply-templates select=\"html-portlet-content\" />\r\n</xsl:template>\r\n	\r\n<xsl:template match=\"html-portlet-content\">\r\n	<xsl:value-of disable-output-escaping=\"yes\" select=\".\" />\r\n</xsl:template>\r\n\r\n</xsl:stylesheet>\r\n\r\n\r\n\r\n\r\n');
INSERT INTO core_stylesheet (id_stylesheet, description, file_name, source) VALUES (282,'Rubrique HTML - Titre en image','portlet_html_title_image.xsl','<?xml version=\"1.0\"?>\r\n<xsl:stylesheet version=\"1.0\" xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\">\r\n\r\n	<xsl:param name=\"mode\" />\r\n\r\n	<xsl:output method=\"html\" indent=\"yes\" />\r\n\r\n	<xsl:template match=\"portlet\">\r\n	\r\n	<xsl:variable name=\"device_class\">\r\n	<xsl:choose>\r\n		<xsl:when test=\"string(display-on-small-device)=\'0\'\">hidden-phone</xsl:when>\r\n		<xsl:otherwise></xsl:otherwise>\r\n	</xsl:choose>\r\n	</xsl:variable>\r\n\r\n		<div class=\"portlet-background-image-on-title {$device_class}\">\r\n			<h1 class=\"portlet-background-image-on-title-header\">\r\n				<xsl:value-of disable-output-escaping=\"yes\" select=\"portlet-name\" />\r\n			</h1>\r\n			<div class=\"portlet-background-image-on-title-content\">\r\n				<xsl:apply-templates select=\"html-portlet\" />\r\n			</div>\r\n		</div>\r\n	</xsl:template>\r\n\r\n	<xsl:template match=\"html-portlet\">\r\n		<xsl:apply-templates select=\"html-portlet-content\" />\r\n	</xsl:template>\r\n\r\n	<xsl:template match=\"html-portlet-content\">\r\n		<xsl:value-of disable-output-escaping=\"yes\" select=\".\" />\r\n	</xsl:template>\r\n\r\n</xsl:stylesheet>');
INSERT INTO core_stylesheet (id_stylesheet, description, file_name, source) VALUES (285,'Rubrique HTML - Fond coloré 1','portlet_html_background.xsl','<?xml version=\"1.0\"?>\r\n<xsl:stylesheet version=\"1.0\" xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\">\r\n\r\n<xsl:output method=\"html\" indent=\"yes\"/>\r\n\r\n<xsl:template match=\"portlet\">\r\n\r\n	<xsl:variable name=\"device_class\">\r\n	<xsl:choose>\r\n		<xsl:when test=\"string(display-on-small-device)=\'0\'\">hidden-phone</xsl:when>\r\n		<xsl:otherwise></xsl:otherwise>\r\n	</xsl:choose>\r\n	</xsl:variable>\r\n	\r\n	<div class=\"portlet-background append-bottom {$device_class}\">\r\n		<xsl:choose>\r\n			<xsl:when test=\"not(string(display-portlet-title)=\'1\')\">\r\n				<h1 class=\"portlet-background-header -lutece-border-radius-top\">\r\n					<xsl:value-of disable-output-escaping=\"yes\" select=\"portlet-name\" />\r\n				</h1>\r\n				<div class=\"portlet-background-content -lutece-border-radius-bottom\">\r\n					<xsl:apply-templates select=\"html-portlet\" />\r\n				</div>\r\n			</xsl:when>\r\n			<xsl:otherwise>\r\n				<div class=\"portlet-background-content -lutece-border-radius\">\r\n					<xsl:apply-templates select=\"html-portlet\" />\r\n				</div>\r\n			</xsl:otherwise>\r\n		</xsl:choose>\r\n    </div>\r\n</xsl:template>\r\n\r\n<xsl:template match=\"html-portlet\">\r\n	<xsl:apply-templates select=\"html-portlet-content\" />\r\n</xsl:template>\r\n\r\n<xsl:template match=\"html-portlet-content\">\r\n	<xsl:value-of disable-output-escaping=\"yes\" select=\".\" />\r\n</xsl:template>\r\n\r\n</xsl:stylesheet>');
INSERT INTO core_stylesheet (id_stylesheet, description, file_name, source) VALUES (291,'Rubrique HTML - Encadré','portlet_html_bordered.xsl','<?xml version=\"1.0\"?>\r\n<xsl:stylesheet version=\"1.0\" xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\">\r\n\r\n<xsl:output method=\"html\" indent=\"yes\"/>\r\n\r\n<xsl:template match=\"portlet\">\r\n	\r\n	<xsl:variable name=\"device_class\">\r\n	<xsl:choose>\r\n		<xsl:when test=\"string(display-on-small-device)=\'0\'\">hidden-phone</xsl:when>\r\n		<xsl:otherwise></xsl:otherwise>\r\n	</xsl:choose>\r\n	</xsl:variable>\r\n\r\n    <div class=\"portlet-bordered  {$device_class\">\r\n    <xsl:if test=\"not(string(display-portlet-title)=\'1\')\">\r\n		<h1 class=\"portlet-bordered-header\">\r\n			<xsl:value-of disable-output-escaping=\"yes\" select=\"portlet-name\" />\r\n		</h1>\r\n    </xsl:if>\r\n        <div class=\"portlet-bordered-content\">\r\n            <xsl:apply-templates select=\"html-portlet\" />\r\n        </div>\r\n    </div>\r\n	<br />\r\n</xsl:template>\r\n\r\n<xsl:template match=\"html-portlet\">\r\n         <xsl:apply-templates select=\"html-portlet-content\" />\r\n</xsl:template>\r\n\r\n<xsl:template match=\"html-portlet-content\">\r\n            <xsl:value-of disable-output-escaping=\"yes\" select=\".\" />\r\n</xsl:template>\r\n\r\n</xsl:stylesheet>\r\n\r\n');
INSERT INTO core_stylesheet (id_stylesheet, description, file_name, source) VALUES (200,'Rubrique HTML - Fond coloré 2','portlet_html_background_colored.xsl','<?xml version=\"1.0\"?>\r\n<xsl:stylesheet version=\"1.0\" xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\">\r\n\r\n<xsl:output method=\"html\" indent=\"yes\"/>\r\n\r\n<xsl:template match=\"portlet\">\r\n\r\n	\r\n	<xsl:variable name=\"device_class\">\r\n	<xsl:choose>\r\n		<xsl:when test=\"string(display-on-small-device)=\'0\'\">hidden-phone</xsl:when>\r\n		<xsl:otherwise></xsl:otherwise>\r\n	</xsl:choose>\r\n	</xsl:variable>\r\n\r\n	<div class=\"portlet-background-colored {$device_class}\">\r\n		<xsl:if test=\"not(string(display-portlet-title)=\'1\')\">\r\n			<div class=\"portlet-background-colored-header\">\r\n			<h1 class=\"portlet-background-colored-header\">\r\n				<xsl:value-of disable-output-escaping=\"yes\" select=\"portlet-name\" />\r\n			</h1>\r\n			</div>\r\n        </xsl:if>\r\n\r\n\r\n        <div class=\"portlet-background-content\">\r\n            <xsl:apply-templates select=\"html-portlet\" />\r\n        </div>\r\n    </div>\r\n</xsl:template>\r\n\r\n<xsl:template match=\"html-portlet\">\r\n         <xsl:apply-templates select=\"html-portlet-content\" />\r\n</xsl:template>\r\n\r\n<xsl:template match=\"html-portlet-content\">\r\n            <xsl:value-of disable-output-escaping=\"yes\" select=\".\" />\r\n</xsl:template>\r\n\r\n</xsl:stylesheet>\r\n\r\n');
