INSERT INTO html_portlet (id_portlet, html) VALUES (91,'<form action=\"jsp/admin/DoAdminLogin.jsp\" method=\"post\">   <h4>&#160;Accès à l\'administration du site</h4>   <div class=\"well\">   <p>     <strong>Choisissez votre profil :</strong>   </p>   <div>   <input id=\"access_code_admin\" title=\"administrateur\" type=\"radio\"   name=\"access_code\" value=\"admin\" checked=\"checked\" />  &nbsp; Administrateur technique</div>   <div>   <input id=\"access_code_webmaster\" title=\"webmaster\" type=\"radio\"   name=\"access_code\" value=\"lutece\" /> &nbsp; Webmaster - Gestion de   contenu</div>   <div>   <input id=\"access_code_redac\" title=\"redacteur\" type=\"radio\"   name=\"access_code\" value=\"redac\" /> &nbsp; Rédacteur - Producteur de   contenu</div>   <div>     <input type=\"hidden\" name=\"password\" value=\"adminadmin\" />   </div>   <br />    <button class=\"btn btn-primary\" type=\"submit\"><i class=\"icon-user icon-white\"></i> &nbsp; Se   connecter</button></div> </form> ');
