INSERT INTO html_portlet (id_portlet, html) VALUES (91,'<form action=\"jsp/admin/DoAdminLogin.jsp\" method=\"post\">\r\n  <h4>&#160;Accès à l\'administration du site</h4>\r\n  <div class=\"well\">\r\n  <p>\r\n    <strong>Choisissez votre profil :</strong>\r\n  </p>\r\n  <div>\r\n  <input id=\"access_code_admin\" title=\"administrateur\" type=\"radio\"\r\n  name=\"access_code\" value=\"admin\" checked=\"checked\" />\r\n &nbsp; Administrateur technique</div>\r\n  <div>\r\n  <input id=\"access_code_webmaster\" title=\"webmaster\" type=\"radio\"\r\n  name=\"access_code\" value=\"lutece\" /> &nbsp; Webmaster - Gestion de\r\n  contenu</div>\r\n  <div>\r\n  <input id=\"access_code_redac\" title=\"redacteur\" type=\"radio\"\r\n  name=\"access_code\" value=\"redac\" /> &nbsp; Rédacteur - Producteur de\r\n  contenu</div>\r\n  <div>\r\n    <input type=\"hidden\" name=\"password\" value=\"adminadmin\" />\r\n  </div>\r\n  <br /> \r\n  <button class=\"btn btn-primary\" type=\"submit\"><i class=\"icon-user icon-white\"></i> &nbsp; Se\r\n  connecter</button></div>\r\n</form>\r\n');
INSERT INTO html_portlet (id_portlet, html) VALUES (92,'<div id=\"myCarousel\" class=\"carousel slide\">\r\n<div class=\"carousel-inner\">\r\n  <div class=\"active item\">\r\n  <img src=\"document?id=21\" alt=\"\" /> \r\n  <div class=\"carousel-caption\">\r\n    <p>Le site de la mairie de Paris</p>\r\n  </div></div>\r\n  <div class=\"item\">\r\n  <img src=\"document?id=14\" alt=\"\" /> \r\n  <div class=\"carousel-caption\">\r\n    <p>Météo France</p>\r\n    <p>&#160;</p>\r\n  </div></div>\r\n  <div class=\"item\">\r\n  <img src=\"document?id=19\" alt=\"\" /> \r\n  <div class=\"carousel-caption\">\r\n    <p>Outil de dématérialisation des documents de séance de\r\n    conseil (municipal ou général)</p>\r\n  </div></div>\r\n  <div class=\"item\">\r\n  <img src=\"document?id=12\" alt=\"\" /> \r\n  <div class=\"carousel-caption\">\r\n    <p>La Mairie de Marseille</p>\r\n  </div></div>\r\n  <div class=\"item\">\r\n  <img src=\"document?id=11\" alt=\"\" /> \r\n  <div class=\"carousel-caption\">\r\n    <p>Les mairie d\'arrondissement à Paris</p>\r\n  </div></div>\r\n  <div class=\"item\">\r\n  <img src=\"document?id=16\" alt=\"\" /> \r\n  <div class=\"carousel-caption\">\r\n    <p>Les télé procédures de la ville de Marseille</p>\r\n  </div></div>\r\n  <div class=\"item\">\r\n  <img src=\"document?id=15\" alt=\"\" /> \r\n  <div class=\"carousel-caption\">\r\n    <p>Eau de Paris</p>\r\n  </div></div>\r\n  <div class=\"item\">\r\n  <img src=\"document?id=13\" alt=\"\" /> \r\n  <div class=\"carousel-caption\">\r\n    <p>Thalys card</p>\r\n  </div></div>\r\n  <div class=\"item\">\r\n  <img src=\"document?id=17\" alt=\"\" /> \r\n  <div class=\"carousel-caption\">\r\n    <p>www.notaires.fr</p>\r\n  </div></div>\r\n  <div class=\"item\">\r\n  <img src=\"document?id=9\" alt=\"\" /> \r\n  <div class=\"carousel-caption\">\r\n    <p>La mairie de Bobigny</p>\r\n  </div></div>\r\n  <div class=\"item\">\r\n  <img src=\"document?id=20\" alt=\"\" /> \r\n  <div class=\"carousel-caption\">\r\